import randomprint("Hoşgeldiniz Pastane reyonuna")# Tartı Sınıfıclass Tarti:    """Kilogram bazlı ürünler için tartı fonksiyonu"""    def __init__(self):        self.hassasiyet = 0.01  # 10 gram hassasiyet    def tart(self, urun_adi):        """Ürünü rastgele bir ağırlıkla tartar ve sonucu döndürür"""        agirlik = round(random.uniform(0.1, 2), 2)  # 100 gram ile 2 kg arası        return f"{urun_adi} için tartılan ağırlık: {agirlik} kg"# Kullanıcıdan Ürün Adını Alma ve Tartmadef urun_tart():    urun_adi = input("Lütfen tartmak istediğiniz ürün adını girin: ").strip()        if urun_adi:        tarti = Tarti()        print(tarti.tart(urun_adi))    else:        print("Geçerli bir ürün adı giriniz!")# Kullanıcıdan giriş alurun_tart()